import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

class ShowPatient extends Component {


    constructor(props) {
        super(props);

    }

     getEncounters(id) {
            axios
                .get(`http://hapi.fhir.org/baseR4/Encounters?_format=json&_pretty=true`)
                .then(res => {

                    var data = []
                    for (let i = 0; i < res.data.entry.length; i++) {
                        if (res.data.entry[i].resource && res.data.entry[i].resource.name) {
                            data.push(res.data.entry[i])
                        }
                    }

                    const slice = data.slice(this.state.offset, this.state.offset + this.state.perPage)

                    const postData = slice.map(pd => <React.Fragment>
                        <p onClick={() => this.handlePatient(pd)}>{pd.resource.name[0].family + " " + pd.resource.name[0].given}</p>
                    </React.Fragment>)

                    this.setState({
                        pageCount: Math.ceil(data.length / this.state.perPage),
                        postData
                    })
                });
        }

    render() {

        return (
            <div class="container">
                <div class="panel panel-default">
                    <div class="panel-heading">
                        <h3 class="panel-title">
                            Patient Details
                        </h3>
                    </div>
                    &emsp;
                    <div class="panel-body">
                        <dl>
                            <dt>Name:</dt>
                            <dd>{this.props.item.resource.name[0].family}</dd>
                            <dt>Encounters:</dt>
                            <dd>{this.props.item.last_name}</dd>
                            <dt>Email:</dt>
                            <dd>{this.props.item.email}</dd>
                            <dt>Gender:</dt>
                            <dd>{this.props.item.gender}</dd>
                            <dt>Diagnosis code:</dt>
                            <dd>{this.props.item.diagnosis_code}</dd>
                            <dt>Diagnosis description:</dt>
                            <dd>{this.props.item.diagnosis_description}</dd>
                            <dt>Diagnosis description detailed:</dt>
                            <dd>{this.props.item.diagnosis_description_detailed}</dd>
                            <dt>Administered drug treatment:</dt>
                            <dd>{this.props.item.administered_drug_treatment}</dd>



                        </dl>
                    </div>
                </div>
            </div>
        );
    }
}

export default ShowPatient;
